// This file contains the core logic for directory scanning
// The CLI binary (bin/cli.js) is auto-generated from this file
// Run 'npm run build' after making changes to regenerate the CLI

const fs = require('fs');
const path = require('path');

/**
 * Scans a directory and returns a list of its subdirectories
 * @param {string} dirPath - The directory path to scan
 * @returns {string[]} Array of subdirectory names
 */
function scanDirectory(dirPath) {
  try {
    if (!fs.existsSync(dirPath)) {
      throw new Error(`Directory does not exist: ${dirPath}`);
    }

    const stats = fs.statSync(dirPath);
    if (!stats.isDirectory()) {
      throw new Error(`Path is not a directory: ${dirPath}`);
    }

    const items = fs.readdirSync(dirPath);

    const directories = items.filter(item => {
      const itemPath = path.join(dirPath, item);
      try {
        const itemStats = fs.statSync(itemPath);
        return itemStats.isDirectory() && !item.startsWith('.');
      } catch (error) {
        return false;
      }
    });

    return directories.sort();
  } catch (error) {
    throw error;
  }
}

module.exports = {
  scanDirectory
};


// CLI execution logic
const targetDir = process.argv[2] || process.cwd();
const absolutePath = path.resolve(targetDir);

console.log(`Scanning directory: ${absolutePath}\n`);

try {
  const directories = scanDirectory(absolutePath);
  
  if (directories.length === 0) {
    console.log('No subdirectories found.');
  } else {
    console.log('Found subdirectories:');
    console.log('-------------------');
    directories.forEach(dir => {
      console.log(`  - ${dir}`);
    });
    console.log(`\nTotal: ${directories.length} subdirectories`);
  }
} catch (error) {
  console.error(`Error: ${error.message}`);
  process.exit(1);
}
